    func getFileData() -> [Campus] {
        // Get the file path to DemoData.json
        if let url = Bundle.main.url(forResource: "DataBase", withExtension: "json"){
            
            do{
                // Read the file and turn it into Data
                let data = try Data(contentsOf: url)
                
                // Parse data into swift instances
                let decoder = JSONDecoder()
                
                do{
                    let cities = try decoder.decode([Campus].self, from: data)
                    return cities
                }
                catch{
                    print("Couldn't parse the JSON: \(error)")
                }
            }
            catch {
                print("Couldn't read the file :\(error)")
            }
            
        }
        
        
        return [Campus]()
        
    }
}
